import React, { Component } from 'react'
import '../css/Layout.css';
import 'antd/dist/antd.css';
import brand from '../images/brand.png';
import axios from 'axios';
import { productListURL} from "../constants";
import Basket from './Basket';
import Slider from '../containers/Slider';
import Filter from './Filter';
import { Menu, Pagination,Icon, Button, Input, AutoComplete} from 'antd';
const { SubMenu } = Menu;

const { Option } = AutoComplete;



class Header extends Component {
    constructor(props) {
        super(props)
        this.state = {
          size: '',
          sort: '', 
          cartItems: [], 
          products: [], 
          filteredProducts: [], 
          dataSource: [],
          loading: false,
          error: null,
          data: [],
          openKeys: ['sub1'],

        }
     }

     rootSubmenuKeys = ['sub1', 'sub2', 'sub4'];

     componentDidMount() {
       this.setState({ loading: true });
       axios
         .get(productListURL)
         .then(res => {
           this.setState({ data: res.data, loading: false });
         })
         .catch(err => {
           this.setState({ error: err, loading: false });
         });
     }

     handleRemoveFromCart = (e, product) => {
         this.setState(state => {
           const cartItems = state.cartItems.filter(a => a.id !== product.id);
           localStorage.setItem('cartItems', JSON.stringify(cartItems));
           return { cartItems: cartItems };
         })
       }

       handleAddToCart = (e, product) => {
         this.setState(state => {
           const cartItems = state.cartItems;
           let productAlreadyInCart = false;

           cartItems.forEach(cp => {
             if (cp.id === product.id) {
               cp.count += 1;
               productAlreadyInCart = true;
             }
           });

           if (!productAlreadyInCart) {
             cartItems.push({ ...product, count: 1 });
           }
           localStorage.setItem('cartItems', JSON.stringify(cartItems));
           return { cartItems: cartItems };
         });
       }

       listProducts = () => {
         this.setState(state => {
           if (state.sort !== '') {
             state.products.sort((a, b) =>
               (state.sort === 'lowestprice'
                 ? ((a.price > b.price) ? 1 : -1)
                 : ((a.price < b.price) ? 1 : -1)));
           } else {
             state.products.sort((a, b) => (a.id > b.id) ? 1 : -1);
           }
           if (state.size !== '') {
             return { filteredProducts: state.product && state.product.filter(a => a.availableSizes.indexOf(state.size.toUpperCase()) >= 0) };
           }
           return { filteredProducts: state.product};
         })
       }

